import { useState , useEffect } from 'react';
import Axios from 'axios'
import { BrowserRouter as Router, Switch, Route, Link } from 'react-router-dom';
import './Header.css';
const Header = () => {
  const Submit = () =>{
    alert('Thank you');
  }
  const [email,setlogin] = useState('');
  const [password,setpassword] = useState('');

  const SubmitLogin = () =>{
    Axios.post('http://localhost:3001/api/insert',{
      email:email,
      password:password
  }).then(()=>{
    alert("Sucessful insert")
  })
  };
  window.addEventListener('scroll',reveal);
        function reveal(){
            var reveals = document.querySelectorAll('.reveal');
            for(var i = 0;i<reveals.length;i++){
                var windowheight = window.innerHeight;
                var revealtop = reveals[i].getBoundingClientRect().top;
                var revealpoint = 150;
                if(revealtop < windowheight - revealpoint){
                    reveals[i].classList.add('active');
                }
                else{
                    reveals[i].classList.remove('active');
                } 
            }
        }
 
  return (
    <header className="Project">
       <nav className='Navbar'>
            <ul>
              <Router>
                <a href="#Container"><li>home</li></a>
                <a href="#Footer"><li>Contact Us</li></a>
                <Route exact path="/Client/src/form.html" render={() => {window.location.href="/Client/src/form.html"}} />
                <a href="#"><li>Payment</li></a>
                <a href="#"><li>Book</li></a>
                <button onClick={Submit}>Order</button>
                </Router>
            </ul>
        </nav>
      <div className='Container' id='Container'>
       
        <center className = "Center">
            <h1>Best 
              <span className='Color'> Shipping</span>
            </h1>
        </center>
        </div>
        <footer>
    <div id="Footer">
        <div className="column reveal">
          <h1 className="Head">Contact Details</h1>
            <div className="column-firt">
            <h1 className='first'><i className="fas fa-phone"></i>9944669700</h1>
            <h1><i className="fas fa-envelope"></i>ijaasahamad2003@gmail.com</h1>
            </div>
            <div className="column-second">
                <form action="get" method="post">
                  <h1>Details</h1>
                   <div className="input">
                      <input type="email" name="email" placeholder="Enter your Email" onChange={(e)=>{
                          setlogin(e.target.value)
                      }} required/>
                   </div>
                   <div className="input">
                      <input type="text" name="password" placeholder="Enter your Number"
                            onChange={(e) =>{
                                setpassword(e.target.value);
                            }}
                            required
                      />
                   </div>
                   <div className="input">
                          <textarea cols={25} rows={5} placeholder="Enter your message" required></textarea>
                   </div>
                   <button type='submit'>Submit</button>
                </form>
            </div>
        </div>
    </div>
    </footer>
    </header>
  )
}

export default Header;
