import { useState , useEffect } from 'react';
import Axios from 'axios'
import './Header.css';
const bodyparser = require('body-parser')
const Header = () => {
  const Submit = () =>{
    alert('Thank you');
  }
  const [email,setlogin] = useState('');
  const [password,setpassword] = useState('');

  const SubmitLogin = () =>{
    Axios.post('http://localhost:3001/api/insert',{
      email:email,
      password:password
  }).then(()=>{
    alert("Sucessful insert")
  })
  };
 
  return (
    <header className="Project">
       <nav className='Navbar'>
            <ul>
                <a href="#"><li>About Us</li></a>
                <a href="#"><li>Contact Us</li></a>
                <a href="#"><li>Services</li></a>
                <a href="#"><li>Payment</li></a>
                <a href="#"><li>Book</li></a>
                <button onClick={Submit}>Order</button>
            </ul>
        </nav>
      <div className='Container'>
       
        <center className = "Center">
            <h1>Best 
              <span className='Color'> Shipping</span>
            </h1>
        </center>
        </div>
        <footer>
    <div id="Footer">
        <div className="column">
          <h1 className="Head">Contact Details</h1>
            <div className="column-firt">
            <h1 ><i className="fas fa-phone"></i>9944669700</h1>
            <h1><i className="fas fa-envelope"></i>ijaasahamad2003@gmail.com</h1>
            </div>
            <div className="column-second">
                <form action="get" method="post">
                  <h1>Details</h1>
                   <div className="input">
                      <input type="email" name="email" placeholder="Enter your Email" onChange={(e)=>{
                          setlogin(e.target.value)
                      }} required/>
                   </div>
                   <div className="input">
                      <input type="text" name="password" placeholder="Enter your Number"
                            onChange={(e) =>{
                                setpassword(e.target.value);
                            }}
                            required
                      />
                   </div>
                   <button>Submit</button>
                </form>
            </div>
        </div>
        
    </div>
    </footer>
    </header>
  )
}
export default Header;
